package SlidingWindow;

/*
    567. 字符串的排列

        给定两个字符串 s1 和 s2，写一个函数来判断 s2 是否包含 s1 的排列。
        换句话说，第一个字符串的排列之一是第二个字符串的 子串 。

    解题思路

        1. 思路:滑动窗口题,窗口在s,判断窗口中是否含有t全部字符且不含其他字符
        2. base case: 若t比s长,直接return false
        3. 初始化tMap记录t中字符出现次数,windowMap记录窗口中相应字符出现次数
        4. 遍历t,初始化tMap
        5. 初始窗口[left,right)为空,左闭右开[0,0)
        6. 设置计数器count
            1. 字符在窗口中出现次数与t中出现次数相等,count++
            2. count=tMap.size(),说明窗口包含t中所有字符且字符出现次数相同
        7. 当窗口右端不越界时移动窗口
            1. 从第一个字符开始,取字符c并扩大窗口右边界
            2. 判断c是否为t中字符
                1. 如果c为t中字符,则将窗口map中相应value+1
                2. 若c在窗口中出现次数与t中出现次数相等,计数器++
                3. 注意:如果字符出现次数>128,Integer就会new对象,因此必须用equals
            3. 如果窗口长度>=t长度,可以收缩左边界
                1. 若窗口包含t中所有字符且字符出现次数相同,返回true
                2. 取字符d并缩小左边界
                3. 判断d是否为t中字符
                    1. 若窗口中出现次数与t中出现次数相等,计数器--
                    2. 如果d为t中字符,则将窗口map中相应value-1
                    3. 注意:先操作计数器,在操作窗口map
        8. 循环直至窗口右端越界扔未找到答案,return false

*/

import java.util.HashMap;
import java.util.Map;
import java.util.Set;

public class B_567 {

    public static void main(String[] args) {

        String t = "";
        String s = "";

        B_567 b_567 = new B_567();
        System.out.println(b_567.checkInclusion(t,s));

    }

    public boolean checkInclusion(String t, String s) {

        //滑动窗口题,窗口在s,判断窗口中是否含有t全部字符且不含其他字符

        // base case
        if (t.length()>s.length()){
            return false;
        }

        //tMap记录t中字符出现次数
        //windowMap记录窗口中相应字符出现次数
        Map<Character,Integer> tMap = new HashMap<>();
        Map<Character,Integer> windowMap = new HashMap<>();

        //遍历t,初始化tMap
        for ( char c : t.toCharArray() ) {
            tMap.put(c, tMap.getOrDefault(c,0)+1);
        }

        //遍历tMap
        Set<Map.Entry<Character,Integer>> entryMap = tMap.entrySet();
        System.out.println("---遍历tMap------------");
        for ( Map.Entry<Character,Integer> node : entryMap             ) {
            System.out.println(node.getKey()+","+node.getValue());
        }

        //初始窗口[left,right)为空,左闭右开[0,0)
        int left = 0, right = 0;

        //设置计数器记录窗口中出现次数与t中出现次数相等的字符数
        //当count=tMap.size(),说明窗口包含t中所有字符且字符出现次数相同
        int count = 0;

        //当窗口右端不越界时移动窗口
        while (right<s.length()){

            System.out.println("扩大窗口循环---------------------");

            //从第一个字符开始,取字符c并扩大窗口右边界
            //窗口为[left,right)
            char c = s.charAt(right);
            right++;
            System.out.println(c+"加入窗口");
            System.out.println("窗口左右边界为"+"["+left+","+right+"]");

            //判断c是否为t中字符
            if (tMap.containsKey(c)){
                //如果c为t中字符,则将窗口map中相应value+1
                windowMap.put(c, windowMap.getOrDefault(c,0)+1);
                //若窗口中出现次数与t中出现次数相等,计数器++
                //注意:如果字符出现次数>128,Integer就会new对象,因此必须用equals
                if (windowMap.get(c).equals(tMap.get(c))){
                    count++;
                }
            }

            //如果窗口长度>=t长度,可以收缩左边界
            while (right-left >= t.length()){

                System.out.println("    收缩窗口循环---------------------");

                //若窗口包含t中所有字符且字符出现次数相同,返回true
                if (count==tMap.size()){
                    //遍历WindowMap
                    Set<Map.Entry<Character,Integer>> entryMap2 = windowMap.entrySet();
                    System.out.println("    ---当前窗口------------");
                    for ( Map.Entry<Character,Integer> node : entryMap2             ) {
                        System.out.println(node.getKey()+","+node.getValue());
                    }
                    return true;
                }

                //取字符d并缩小左边界
                char d = s.charAt(left);
                left++;
                System.out.println("    "+d+"移出窗口");
                System.out.println("    窗口左右边界为"+"["+left+","+right+"]");

                //判断d是否为t中字符
                if (tMap.containsKey(d)){
                    //若窗口中出现次数与t中出现次数相等,计数器--
                    //注意:如果字符出现次数>128,Integer就会new对象,因此必须用equals
                    if (windowMap.get(d).equals(tMap.get(d))){
                        count--;
                    }
                    //如果d为t中字符,则将窗口map中相应value-1
                    windowMap.put(d, windowMap.getOrDefault(d,0)-1);

                }

            }

        }

        return false;

    }

}
